// src/pages/Games.js

const games = [
  {
    title: "Valorant",
    image: "valorant.jpg",
    platform: "PC",
  },
  {
    title: "Call of Duty",
    image:
      "https://cdn.cloudflare.steamstatic.com/steam/apps/1938090/header.jpg",
    platform: "Xbox",
  },
  {
    title: "FIFA 24",
    image:
      "https://cdn.cloudflare.steamstatic.com/steam/apps/2195250/header.jpg",
    platform: "PC / Console",
  },
  {
    title: "Elden Ring",
    image:
      "https://cdn.cloudflare.steamstatic.com/steam/apps/1245620/header.jpg",
    platform: "PS5 / Xbox",
  },
];

const Games = () => {
  return (
    <div className="p-6 text-white bg-[#0e0e1a] min-h-screen">
      <h2 className="text-3xl font-bold mb-6 border-b border-purple-500 pb-2">
        ðŸŽ® Popular Games
      </h2>

      <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4">
        {games.map((game, index) => (
          <div
            key={index}
            className="bg-[#1c1c2b] rounded-xl overflow-hidden shadow-lg transform transition hover:scale-105 hover:shadow-purple-500/40"
          >
            <img
              src={game.image}
              alt={game.title}
              className="w-full h-48 object-cover"
            />
            <div className="p-4">
              <h3 className="text-xl font-semibold">{game.title}</h3>
              <p className="text-sm text-purple-300 mt-1">{game.platform}</p>
              <button className="mt-4 px-4 py-2 bg-purple-600 hover:bg-purple-700 rounded text-sm">
                BOOK
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Games;
