import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const games = [
  { id: 1, name: "Call of Duty", price: 220, image: "/images/cod.jpg" },
  { id: 2, name: "FIFA 24", price: 185, image: "/images/fifa.jpg" },
  { id: 3, name: "Elden Ring", price: 255, image: "/images/eldenring.jpg" },
  { id: 4, name: "GTA V", price: 145, image: "/images/gta.jpg" },
  { id: 5, name: "Valorant Points Pack", price: 73, image: "/images/valorant.jpg" },
  { id: 6, name: "Red Dead Redemption 2", price: 240, image: "/images/rdr2.jpg" },
  { id: 7, name: "Assassin's Creed Mirage", price: 220, image: "/images/acmirage.jpg" },
  { id: 8, name: "Spider-Man: Miles Morales", price: 205, image: "/images/spiderman.jpg" },
];

const accessories = [
  { id: 9, name: "Logitech G Pro X Headset", price: 330, image: "/images/logitech_headset.jpg" },
  { id: 10, name: "Razer DeathAdder Mouse", price: 180, image: "/images/razer_mouse.jpg" },
  { id: 11, name: "SteelSeries Apex Pro Keyboard", price: 660, image: "/images/steelseries_keyboard.jpg" },
  { id: 12, name: "Corsair MM300 Mousepad", price: 110, image: "/images/mousepad.jpg" },
  { id: 13, name: "HyperX Cloud Alpha", price: 367, image: "/images/hyperx.jpg" },
  { id: 14, name: "Elgato Stream Deck Mini", price: 330, image: "/images/streamdeck.jpg" },
  { id: 15, name: "Gaming Chair - Secretlab", price: 1465, image: "/images/secretlab.jpg" },
  { id: 16, name: "RGB LED Strip Lights", price: 73, image: "/images/ledstrip.jpg" },
];

const gear = [
  { id: 17, name: "RTX 4080 GPU", price: 4400, image: "/images/gpu.jpg" },
  { id: 18, name: "PS5 Console", price: 1835, image: "/images/ps5.jpg" },
  { id: 19, name: "Custom PC Build", price: 5150, image: "/images/pcbuild.jpg" },
  { id: 20, name: "Xbox Series X", price: 1650, image: "/images/xbox.jpg" },
  { id: 21, name: "Alienware 27‚Äù Monitor", price: 2200, image: "/images/alienware_monitor.jpg" },
  { id: 22, name: "Intel i9 Processor", price: 1835, image: "/images/intel_i9.jpg" },
  { id: 23, name: "ASUS ROG Motherboard", price: 1100, image: "/images/asus_motherboard.jpg" },
  { id: 24, name: "Samsung SSD 2TB", price: 845, image: "/images/samsung_ssd.jpg" },
];

const Shop = () => {
  const [activeTab, setActiveTab] = useState("games");
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const navigate = useNavigate();

  const getProducts = () => {
    if (activeTab === "games") return games;
    if (activeTab === "accessories") return accessories;
    return gear;
  };

  const addToCart = (product) => {
    setCart([...cart, product]);
    setShowCart(true);
  };

  const removeFromCart = (indexToRemove) => {
    setCart(cart.filter((_, index) => index !== indexToRemove));
  };

  const total = cart.reduce((acc, item) => acc + item.price, 0).toFixed(2);

  return (
    <div className="pt-20 px-6 pb-10 bg-gray-900 min-h-screen text-white">
      <div className="flex justify-between items-center flex-wrap mt-5 mb-8">
        <div className="w-full sm:w-auto flex justify-center flex-1 gap-4">
          <button
            onClick={() => setActiveTab("games")}
            className={`px-4 py-2 rounded-full ${activeTab === "games" ? "bg-blue-600" : "bg-gray-700"} hover:bg-blue-700`}
          >
            üéÆ Games
          </button>
          <button
            onClick={() => setActiveTab("accessories")}
            className={`px-4 py-2 rounded-full ${activeTab === "accessories" ? "bg-blue-600" : "bg-gray-700"} hover:bg-blue-700`}
          >
            üéß Accessories
          </button>
          <button
            onClick={() => setActiveTab("gear")}
            className={`px-4 py-2 rounded-full ${activeTab === "gear" ? "bg-blue-600" : "bg-gray-700"} hover:bg-blue-700`}
          >
            üñ•Ô∏è Gear
          </button>
        </div>

        <div className="mt-4 sm:mt-0">
          <button
            onClick={() => setShowCart(true)}
            className="bg-yellow-500 hover:bg-yellow-600 text-black font-bold py-2 px-4 rounded-full"
          >
            üõí Cart ({cart.length})
          </button>
        </div>
      </div>

      <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 gap-4">
        {getProducts().map((product) => (
          <div
            key={product.id}
            className="bg-gray-800 rounded-lg p-3 shadow hover:shadow-md transition w-full"
          >
            <img
              src={product.image}
              alt={product.name}
              className="w-full h-36 object-cover rounded mb-3"
            />
            <h2 className="text-base font-semibold leading-tight">{product.name}</h2>
            <p className="text-sm text-green-400 mb-2">AED {product.price}</p>
            <button
              onClick={() => addToCart(product)}
              className="w-full px-3 py-1.5 bg-yellow-500 hover:bg-yellow-600 text-sm rounded font-semibold text-black"
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>

      {showCart && (
        <div className="fixed right-0 top-0 h-full w-80 bg-gray-800 text-white shadow-lg p-6 z-50">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-2xl font-bold">Your Cart</h2>
            <button
              onClick={() => setShowCart(false)}
              className="text-gray-300 hover:text-white text-sm"
            >
              ‚¨Ö Back
            </button>
          </div>

          {cart.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            <ul>
              {cart.map((item, index) => (
                <li
                  key={index}
                  className="mb-2 flex justify-between items-center"
                >
                  <span>{item.name} - AED {item.price}</span>
                  <button
                    onClick={() => removeFromCart(index)}
                    className="text-red-400 hover:text-red-600 text-sm ml-2"
                  >
                    ‚ùå
                  </button>
                </li>
              ))}
            </ul>
          )}

          <hr className="my-4 border-gray-600" />
          <p className="text-xl font-semibold">Total: AED {total}</p>
          <button
            onClick={() => {
              navigate("/checkout", { state: { cart, total } });
            }}
            className="mt-4 w-full bg-green-500 hover:bg-green-600 py-2 rounded-lg"
          >
            Checkout
          </button>
        </div>
      )}
    </div>
  );
};

export default Shop;
