import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [paymentMethod, setPaymentMethod] = useState("card");
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    if (location.state?.cart && location.state?.total) {
      setCart(location.state.cart);
      setTotal(location.state.total);
    }
  }, [location.state]);

  const handleRemoveItem = (indexToRemove) => {
    const newCart = cart.filter((_, index) => index !== indexToRemove);
    setCart(newCart);
    const newTotal = newCart
      .reduce((acc, item) => acc + item.price, 0)
      .toFixed(2);
    setTotal(newTotal);
  };

  const handlePlaceOrder = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/orders", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          items: cart.map(({ name, price }) => ({ name, price })),
          total: Number(total),
        }),
      });

      const data = await response.json();

      if (response.ok) {
        navigate("/success", { state: { cart, total, paymentMethod } });
      } else {
        alert("Order failed: " + data.message);
      }
    } catch (err) {
      alert("Error placing order.");
      console.error(err);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-900 p-4">
      <div className="bg-gray-800 p-8 rounded-lg shadow-lg w-full max-w-md text-white">
        <h1 className="text-2xl font-bold mb-6 text-center">Checkout</h1>

        <div className="mb-4">
          <label className="block mb-2 font-semibold">
            Select Payment Method:
          </label>
          <select
            className="w-full p-2 rounded text-black"
            value={paymentMethod}
            onChange={(e) => setPaymentMethod(e.target.value)}
          >
            <option value="card">Card</option>
            <option value="paypal">PayPal</option>
            <option value="cash">Cash on Delivery</option>
          </select>
        </div>

        <div className="mb-4">
          <h2 className="font-semibold mb-2">Your Items:</h2>
          {cart.length === 0 ? (
            <p className="text-red-400">No items in the cart.</p>
          ) : (
            <ul className="space-y-2">
              {cart.map((item, index) => (
                <li key={index} className="flex justify-between items-center">
                  <span>
                    {item.name} - AED {item.price}
                  </span>
                  <button
                    onClick={() => handleRemoveItem(index)}
                    className="text-red-400 hover:text-red-600 text-sm"
                  >
                    ‚ùå
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>

        <p className="text-lg font-semibold mb-4">Total: AED {total}</p>

        <button
          onClick={handlePlaceOrder}
          className="w-full bg-green-500 hover:bg-green-600 py-2 rounded font-semibold"
        >
          Place Order
        </button>
      </div>
    </div>
  );
};

export default Checkout;
