import { gapi } from "gapi-script";

const CLIENT_ID =
  "328701998111-tmisd20ksfna4driqc6u2kj597j7cp0s.apps.googleusercontent.com";

const SCOPES = "https://www.googleapis.com/auth/calendar.events";
const DISCOVERY_DOC =
  "https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest";

export const initializeGapi = () => {
  gapi.load("client:auth2", () => {
    gapi.client
      .init({
        clientId: CLIENT_ID,
        discoveryDocs: [DISCOVERY_DOC],
        scope: SCOPES,
      })
      .then(() => {
        console.log("‚úÖ GAPI initialized");
      });
  });
};

export const signInWithGoogle = async () => {
  const authInstance = gapi.auth2.getAuthInstance();
  const user = await authInstance.signIn();
  return user;
};

export const createCalendarEvent = async (event) => {
  try {
    const request = gapi.client.calendar.events.insert({
      calendarId: "primary",
      resource: event,
    });

    const response = await request.execute();
    console.log("üìÖ Event created:", response.htmlLink);
  } catch (err) {
    console.error("‚ùå Calendar Error:", err);
  }
};
