import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [paymentMethod, setPaymentMethod] = useState("card");
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    if (location.state?.cart && location.state?.total) {
      setCart(location.state.cart);
      setTotal(location.state.total);
    }
  }, [location.state]);

  const handlePlaceOrder = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/orders", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          items: cart.map(({ name, price }) => ({ name, price })),
          total: Number(total),
        }),
      });

      const data = await response.json();

      if (response.ok) {
        navigate("/success", { state: { cart, total, paymentMethod } });
      } else {
        alert("Order failed: " + data.message);
      }
    } catch (err) {
      alert("Error placing order.");
      console.error(err);
    }
  };

  return (
    <div className="p-6 text-white bg-gray-900 min-h-screen">
      <h1 className="text-2xl font-bold mb-4">Checkout</h1>

      <h2 className="mb-2">Select Payment Method:</h2>
      <select
        className="mb-4 p-2 rounded text-black"
        value={paymentMethod}
        onChange={(e) => setPaymentMethod(e.target.value)}
      >
        <option value="card">Card</option>
        <option value="paypal">PayPal</option>
        <option value="cash">Cash on Delivery</option>
      </select>

      <h2 className="mb-2 font-semibold">Your Items:</h2>
      <ul className="mb-4">
        {cart.map((item, index) => (
          <li key={index}>
            {item.name} - AED {item.price}
          </li>
        ))}
      </ul>

      <p className="text-lg font-semibold">Total: AED {total}</p>

      <button
        onClick={handlePlaceOrder}
        className="mt-4 bg-green-500 px-4 py-2 rounded hover:bg-green-600"
      >
        Place Order
      </button>
    </div>
  );
};

export default Checkout;
