import React, { useState } from "react";
import emailjs from "@emailjs/browser";

const Booking = () => {
  const [activeTab, setActiveTab] = useState("gaming");
  const [gamingForm, setGamingForm] = useState({
    pc: "",
    time: "",
    name: "",
    email: "",
  });

  const [tournamentForm, setTournamentForm] = useState({
    game: "",
    tournamentName: "",
    mode: "solo",
    teamSize: "",
    dateTime: "",
    name: "",
    email: "",
  });

  const handleGamingChange = (e) => {
    setGamingForm({ ...gamingForm, [e.target.name]: e.target.value });
  };

  const handleTournamentChange = (e) => {
    setTournamentForm({ ...tournamentForm, [e.target.name]: e.target.value });
  };

  const handleGamingSubmit = (e) => {
    e.preventDefault();
    emailjs
      .send(
        "service_h5q5lwk",
        "template_b3eg6ds",
        {
          ...gamingForm,
          to_email: gamingForm.email,
        },
        "LEjNbva7cItXZoDDR"
      )
      .then(() => {
        alert(`‚úÖ Booking confirmed for ${gamingForm.pc}`);
        setGamingForm({ pc: "", time: "", name: "", email: "" });
      })
      .catch((err) => {
        alert("‚ùå Booking failed");
        console.error(err);
      });
  };

  const handleTournamentSubmit = (e) => {
    e.preventDefault();
    emailjs
      .send(
        "service_h5q5lwk",
        "template_b3eg6ds",
        {
          ...tournamentForm,
          to_email: tournamentForm.email,
        },
        "LEjNbva7cItXZoDDR"
      )
      .then(() => {
        alert(`‚úÖ Tournament slot booked for ${tournamentForm.tournamentName}`);
        setTournamentForm({
          game: "",
          tournamentName: "",
          mode: "solo",
          teamSize: "",
          dateTime: "",
          name: "",
          email: "",
        });
      })
      .catch((err) => {
        alert("‚ùå Booking failed");
        console.error(err);
      });
  };

  return (
    <section className="bg-gray-950 text-white min-h-screen flex flex-col justify-center pt-28 pb-20 px-4 sm:px-6 lg:px-8">
      <div className="max-w-2xl mx-auto w-full">
        <div className="flex justify-center gap-4 mb-10">
          <button
            onClick={() => setActiveTab("gaming")}
            className={`px-6 py-2 rounded-full font-semibold transition ${
              activeTab === "gaming"
                ? "bg-emerald-500 text-black"
                : "bg-gray-800 border border-gray-700"
            }`}
          >
            Gaming Slot
          </button>
          <button
            onClick={() => setActiveTab("tournament")}
            className={`px-6 py-2 rounded-full font-semibold transition ${
              activeTab === "tournament"
                ? "bg-emerald-500 text-black"
                : "bg-gray-800 border border-gray-700"
            }`}
          >
            Tournament Slot
          </button>
        </div>

        {/* Gaming Form */}
        {activeTab === "gaming" && (
          <form
            onSubmit={handleGamingSubmit}
            className="bg-gray-900 border border-gray-800 p-8 rounded-2xl shadow-xl space-y-6"
          >
            <h2 className="text-2xl font-bold text-center text-emerald-400 mb-4">
              Book Your Gaming Slot üéÆ
            </h2>

            {/* PC Selection */}
            <select
              name="pc"
              value={gamingForm.pc}
              onChange={handleGamingChange}
              required
              className="w-full p-3 bg-gray-800 text-white rounded border border-gray-700"
            >
              <option value="">-- Choose a PC --</option>
              {Array.from({ length: 10 }, (_, i) => (
                <option key={i} value={`PC ${i + 1}`}>{`PC ${i + 1}`}</option>
              ))}
            </select>

            {/* Time Slot */}
            <select
              name="time"
              value={gamingForm.time}
              onChange={handleGamingChange}
              required
              className="w-full p-3 bg-gray-800 text-white rounded border border-gray-700"
            >
              <option value="">-- Choose a time --</option>
              {[
                "12:00 PM - 2:00 PM",
                "2:00 PM - 4:00 PM",
                "4:00 PM - 6:00 PM",
                "6:00 PM - 8:00 PM",
                "8:00 PM - 10:00 PM",
              ].map((slot) => (
                <option key={slot} value={slot}>
                  {slot}
                </option>
              ))}
            </select>

            {/* Name & Email */}
            <input
              type="text"
              name="name"
              placeholder="Your Name"
              value={gamingForm.name}
              onChange={handleGamingChange}
              required
              className="w-full p-3 bg-gray-800 text-white rounded border border-gray-700"
            />
            <input
              type="email"
              name="email"
              placeholder="you@example.com"
              value={gamingForm.email}
              onChange={handleGamingChange}
              required
              className="w-full p-3 bg-gray-800 text-white rounded border border-gray-700"
            />

            <button
              type="submit"
              className="w-full bg-emerald-500 hover:bg-emerald-400 text-black font-bold py-3 rounded-md"
            >
              Confirm Booking
            </button>
          </form>
        )}

        {/* Tournament Form */}
        {activeTab === "tournament" && (
          <form
            onSubmit={handleTournamentSubmit}
            className="bg-gray-900 border border-gray-800 p-8 rounded-2xl shadow-xl space-y-6"
          >
            <h2 className="text-2xl font-bold text-center text-emerald-400 mb-4">
              Book Your Tournament Slot üèÜ
            </h2>

            <input
              name="game"
              placeholder="Game Name"
              value={tournamentForm.game}
              onChange={handleTournamentChange}
              required
              className="w-full p-3 bg-gray-800 text-white rounded border border-gray-700"
            />

            <input
              name="tournamentName"
              placeholder="Tournament Name"
              value={tournamentForm.tournamentName}
              onChange={handleTournamentChange}
              required
              className="w-full p-3 bg-gray-800 text-white rounded border border-gray-700"
            />

            <select
              name="mode"
              value={tournamentForm.mode}
              onChange={handleTournamentChange}
              required
              className="w-full p-3 bg-gray-800 text-white rounded border border-gray-700"
            >
              <option value="solo">Solo</option>
              <option value="team">Team</option>
            </select>

            {tournamentForm.mode === "team" && (
              <input
                name="teamSize"
                type="number"
                placeholder="Number of Players"
                value={tournamentForm.teamSize}
                onChange={handleTournamentChange}
                required
                className="w-full p-3 bg-gray-800 text-white rounded border border-gray-700"
              />
            )}

            <input
              name="dateTime"
              type="datetime-local"
              value={tournamentForm.dateTime}
              onChange={handleTournamentChange}
              required
              className="w-full p-3 bg-gray-800 text-white rounded border border-gray-700"
            />

            <input
              name="name"
              placeholder="Player Name"
              value={tournamentForm.name}
              onChange={handleTournamentChange}
              required
              className="w-full p-3 bg-gray-800 text-white rounded border border-gray-700"
            />

            <input
              name="email"
              placeholder="Player Email"
              type="email"
              value={tournamentForm.email}
              onChange={handleTournamentChange}
              required
              className="w-full p-3 bg-gray-800 text-white rounded border border-gray-700"
            />

            <button
              type="submit"
              className="w-full bg-emerald-500 hover:bg-emerald-400 text-black font-bold py-3 rounded-md"
            >
              Submit Tournament Entry
            </button>
          </form>
        )}
      </div>
    </section>
  );
};

export default Booking;
