const CLIENT_ID =
  "328701998111-tmisd20ksfna4driqc6u2kj597j7cp0s.apps.googleusercontent.com";
const API_KEY = ""; // Not required for Calendar API, can leave empty
const SCOPES = "https://www.googleapis.com/auth/calendar.events";

export const initializeGapi = () => {
  window.gapi.load("client:auth2", () => {
    window.gapi.client.init({
      clientId: CLIENT_ID,
      scope: SCOPES,
    });
  });
};

export const signInWithGoogle = async () => {
  await window.gapi.auth2.getAuthInstance().signIn();
};

export const createCalendarEvent = async (event) => {
  await window.gapi.client.load("calendar", "v3");

  const response = await window.gapi.client.calendar.events.insert({
    calendarId: "primary",
    resource: event,
  });

  console.log("Calendar event created:", response);
  return response;
};
