import React, { useState } from "react";

const games = [
  { id: 1, name: "Call of Duty", price: 59.99, image: "/images/cod.jpg" },
  { id: 2, name: "FIFA 24", price: 49.99, image: "/images/fifa.jpg" },
  { id: 3, name: "Elden Ring", price: 69.99, image: "/images/eldenring.jpg" },
  { id: 4, name: "GTA V", price: 39.99, image: "/images/gta.jpg" },
];

const accessories = [
  { id: 5, name: "Gaming Mouse", price: 29.99, image: "/images/mouse.jpg" },
  {
    id: 6,
    name: "Mechanical Keyboard",
    price: 79.99,
    image: "/images/keyboard.jpg",
  },
  { id: 7, name: "Gaming Headset", price: 59.99, image: "/images/headset.jpg" },
];

const gear = [
  { id: 8, name: "RTX 4080 GPU", price: 1199.99, image: "/images/gpu.jpg" },
  { id: 9, name: "PS5 Console", price: 499.99, image: "/images/ps5.jpg" },
  {
    id: 10,
    name: "Custom PC Build",
    price: 1399.99,
    image: "/images/pcbuild.jpg",
  },
];

const Shop = () => {
  const [activeTab, setActiveTab] = useState("games");
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);

  const getProducts = () => {
    if (activeTab === "games") return games;
    if (activeTab === "accessories") return accessories;
    return gear;
  };

  const addToCart = (product) => {
    setCart([...cart, product]);
    setShowCart(true); // Auto open cart
  };

  const total = cart.reduce((acc, item) => acc + item.price, 0).toFixed(2);

  return (
    <div className="pt-20 px-6 pb-10 bg-gray-900 min-h-screen text-white">
      {/* Toggle Buttons */}
      <div className="flex justify-center gap-4 mt-5 mb-8">
        <button
          onClick={() => setActiveTab("games")}
          className={`px-4 py-2 rounded-full ${
            activeTab === "games" ? "bg-blue-600" : "bg-gray-700"
          } hover:bg-blue-700`}
        >
          🎮 Games
        </button>
        <button
          onClick={() => setActiveTab("accessories")}
          className={`px-4 py-2 rounded-full ${
            activeTab === "accessories" ? "bg-blue-600" : "bg-gray-700"
          } hover:bg-blue-700`}
        >
          🎧 Accessories
        </button>
        <button
          onClick={() => setActiveTab("gear")}
          className={`px-4 py-2 rounded-full ${
            activeTab === "gear" ? "bg-blue-600" : "bg-gray-700"
          } hover:bg-blue-700`}
        >
          🖥️ Gear
        </button>
      </div>

      {/* Cart Button */}
      <div className="flex justify-end mb-4">
        <button
          onClick={() => setShowCart(true)}
          className="bg-yellow-500 hover:bg-yellow-600 text-black font-bold py-2 px-4 rounded-full"
        >
          🛒 Cart ({cart.length})
        </button>
      </div>

      {/* Product Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {getProducts().map((product) => (
          <div
            key={product.id}
            className="bg-gray-800 rounded-xl p-4 shadow-md hover:shadow-lg transition"
          >
            <img
              src={product.image}
              alt={product.name}
              className="w-full h-48 object-cover rounded-lg mb-4"
            />
            <h2 className="text-lg font-semibold">{product.name}</h2>
            <p className="text-green-400">${product.price}</p>
            <button
              onClick={() => addToCart(product)}
              className="mt-3 px-4 py-2 bg-yellow-500 hover:bg-yellow-600 rounded-lg text-black font-semibold"
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>

      {/* Cart Sidebar */}
      {showCart && (
        <div className="fixed right-0 top-0 h-full w-80 bg-gray-800 text-white shadow-lg p-6 z-50">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-2xl font-bold">Your Cart</h2>
            <button
              onClick={() => setShowCart(false)}
              className="text-gray-300 hover:text-white text-sm"
            >
              ⬅ Back
            </button>
          </div>

          {cart.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            <ul>
              {cart.map((item, index) => (
                <li key={index} className="mb-2">
                  {item.name} - ${item.price}
                </li>
              ))}
            </ul>
          )}
          <hr className="my-4 border-gray-600" />
          <p className="text-xl font-semibold">Total: ${total}</p>
          <button
            onClick={() => alert("Checkout coming soon!")}
            className="mt-4 w-full bg-green-500 hover:bg-green-600 py-2 rounded-lg"
          >
            Checkout
          </button>
        </div>
      )}
    </div>
  );
};

export default Shop;
