import React from "react";
import { useLocation, Link } from "react-router-dom";

const Success = () => {
  const location = useLocation();
  const { cart, total, paymentMethod } = location.state || {};

  return (
    <div className="p-6 text-white bg-gray-900 min-h-screen">
      <h1 className="text-2xl font-bold mb-4">Order Successful ðŸŽ‰</h1>
      <p className="mb-2">Payment Method: {paymentMethod}</p>
      <p className="mb-2 font-semibold">Your Order:</p>
      <ul className="mb-4">
        {cart?.map((item, index) => (
          <li key={index}>
            {item.name} - AED {item.price}
          </li>
        ))}
      </ul>
      <p className="text-lg font-bold">Total Paid: AED {total}</p>

      <Link
        to="/"
        className="inline-block mt-6 bg-yellow-500 px-4 py-2 rounded text-black hover:bg-yellow-600"
      >
        Back to Shop
      </Link>
    </div>
  );
};

export default Success;
