import { Link, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import { auth } from "../firebase";
import { onAuthStateChanged, signOut } from "firebase/auth";

const Navbar = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });
    return () => unsubscribe();
  }, []);

  const handleLogout = async () => {
    try {
      await signOut(auth);
      alert("Logged out successfully.");
      navigate("/auth");
    } catch (error) {
      console.error("Logout failed:", error.message);
    }
  };

  return (
    <nav className="bg-black text-white px-8 py-4 fixed top-0 left-0 w-full z-50">
      <div className="flex items-center justify-between">
        {/* Left: Logo and Name */}
        <div className="flex items-center">
          <img
            src="/RenderXSvgLogo.svg"
            alt="Render-X Logo"
            className="h-20 w-auto object-contain max-h-12"
          />
          <Link to="/home">
            <span className="text-2xl font-extrabold text-emerald-400">
              Render-X
            </span>
          </Link>
        </div>

        {/* Center: Navigation Links */}
        <ul className="flex space-x-8 text-lg items-center">
          {["Home", "Games", "Tournaments", "Booking", "Shop"].map((item) => (
            <li key={item}>
              <Link
                to={`/${item.toLowerCase()}`}
                className="relative group transition"
              >
                {item}
                <span className="absolute left-0 -bottom-1 w-0 h-0.5 bg-blue-500 transition-all duration-300 group-hover:w-full"></span>
              </Link>
            </li>
          ))}
        </ul>

        {/* Right: Auth or Avatar */}
        <ul className="flex space-x-6 text-lg items-center relative">
          {user ? (
            <div className="group relative">
              <img
                src={user.photoURL || "useravatar.jpg"}
                alt="User Avatar"
                className="h-10 w-10 rounded-full border-2 border-emerald-400 cursor-pointer"
              />
              <div className="absolute top-12 right-0 hidden group-hover:flex flex-col bg-white text-black rounded shadow-lg p-2">
                <span className="text-sm px-3 py-1">
                  {user.displayName || "Anonymous"}
                </span>
                <button
                  onClick={handleLogout}
                  className="text-left px-3 py-1 hover:bg-gray-200 text-red-600"
                >
                  Logout
                </button>
              </div>
            </div>
          ) : (
            <li>
              <Link to="/auth" className="relative group transition">
                Register / Login
                <span className="absolute left-0 -bottom-1 w-0 h-0.5 bg-blue-500 transition-all duration-300 group-hover:w-full"></span>
              </Link>
            </li>
          )}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
