// src/pages/Shop.js
import React, { useState } from "react";

const products = [
  {
    id: 1,
    name: "Call of Duty: Modern Warfare",
    price: 59.99,
    image: "/images/cod.jpg",
  },
  {
    id: 2,
    name: "FIFA 24",
    price: 49.99,
    image: "/images/fifa.jpg",
  },
  {
    id: 3,
    name: "Elden Ring",
    price: 69.99,
    image: "/images/eldenring.jpg",
  },
  {
    id: 4,
    name: "GTA V (PC)",
    price: 39.99,
    image: "/images/gta.jpg",
  },
];

const Shop = () => {
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  const total = cart.reduce((acc, item) => acc + item.price, 0).toFixed(2);

  return (
    <div className="p-8 bg-gray-900 min-h-screen text-white">
      <h1 className="text-4xl font-bold mb-6 text-center">Shop Games</h1>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {products.map((product) => (
          <div
            key={product.id}
            className="bg-gray-800 rounded-xl p-4 shadow-md hover:shadow-lg transition"
          >
            <img
              src={product.image}
              alt={product.name}
              className="w-full h-48 object-cover rounded-lg mb-4"
            />
            <h2 className="text-lg font-semibold">{product.name}</h2>
            <p className="text-green-400">${product.price}</p>
            <button
              onClick={() => addToCart(product)}
              className="mt-3 px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg text-white"
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>

      {/* Cart Button */}
      <button
        onClick={() => setShowCart(!showCart)}
        className="fixed top-5 right-5 bg-yellow-500 hover:bg-yellow-600 text-black font-bold py-2 px-4 rounded-full z-50"
      >
        ðŸ›’ Cart ({cart.length})
      </button>

      {/* Cart Sidebar */}
      {showCart && (
        <div className="fixed right-0 top-0 h-full w-80 bg-gray-800 text-white shadow-lg p-6 z-40">
          <h2 className="text-2xl font-bold mb-4">Your Cart</h2>
          {cart.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            <ul>
              {cart.map((item, index) => (
                <li key={index} className="mb-2">
                  {item.name} - ${item.price}
                </li>
              ))}
            </ul>
          )}
          <hr className="my-4 border-gray-600" />
          <p className="text-xl font-semibold">Total: ${total}</p>
          <button
            onClick={() => alert("Checkout coming soon!")}
            className="mt-4 w-full bg-green-500 hover:bg-green-600 py-2 rounded-lg"
          >
            Checkout
          </button>
        </div>
      )}
    </div>
  );
};

export default Shop;
