// src/pages/Profile.js
import React, { useEffect, useState } from "react";
import { auth, db } from "../firebase";
import { doc, getDoc } from "firebase/firestore";
import { useNavigate } from "react-router-dom";

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      const user = auth.currentUser;
      if (!user) return navigate("/auth");

      const ref = doc(db, "users", user.uid);
      const snap = await getDoc(ref);
      if (snap.exists()) {
        setUserData(snap.data());
      }
    };

    fetchUser();
  }, [navigate]);

  if (!userData)
    return <p className="text-center text-white mt-20">Loading...</p>;

  return (
    <div className="flex flex-col items-center mt-24 text-white">
      <div className="bg-gray-900 p-8 rounded-xl shadow-lg w-[90%] max-w-md text-center">
        <div className="flex justify-center mb-4">
          <img
            src={userData.photo || "/useravatar.jpg"}
            alt="User Icon"
            className="h-24 w-24 rounded-full border-4 border-emerald-400 object-cover"
          />
        </div>
        <h2 className="text-2xl font-bold">{userData.name || "Anonymous"}</h2>
        <p className="text-sm text-gray-300">{userData.email}</p>
        <p className="text-xs mt-2 text-gray-500">UID: {userData.uid}</p>
      </div>
    </div>
  );
};

export default Profile;
