import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";

const stripePromise = loadStripe(
  "pk_test_51RUd79Rsx8wcTqfW6w2xH7VlUOsRz..." // Replace with your real Stripe public key
);

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [paymentMethod, setPaymentMethod] = useState("card");
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    if (location.state?.cart && location.state?.total) {
      setCart(location.state.cart);
      setTotal(location.state.total);
    }
  }, [location.state]);

  const handleRemoveItem = (indexToRemove) => {
    const newCart = cart.filter((_, index) => index !== indexToRemove);
    setCart(newCart);
    const newTotal = newCart
      .reduce((acc, item) => acc + item.price, 0)
      .toFixed(2);
    setTotal(newTotal);
  };

  const handlePlaceOrder = async () => {
    try {
      const stripe = await stripePromise;

      const paymentRes = await fetch(
        "http://localhost:5000/api/payments/create-payment",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Cache-Control": "no-cache", // ✅ Disable caching
          },
          body: JSON.stringify({ total }),
        }
      );

      const paymentData = await paymentRes.json();
      console.log("Payment Data:", paymentData);

      if (!paymentData.clientSecret) {
        alert("Payment failed to initialize.");
        return;
      }

      // Proceed to save the order after payment initialized
      const orderRes = await fetch("http://localhost:5000/api/orders", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          items: cart.map(({ name, price }) => ({ name, price })),
          total: Number(total),
        }),
      });

      const orderData = await orderRes.json();

      if (orderRes.ok) {
        navigate("/success", { state: { cart, total, paymentMethod } });
      } else {
        alert("Order failed: " + orderData.message);
      }
    } catch (err) {
      alert("Error during payment/order process.");
      console.error(err);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-900 p-4">
      <div className="w-full max-w-md">
        {/* Back Button */}
        <div className="mb-4">
          <button
            onClick={() => navigate("/shop")}s
            className="bg-yellow-500 text-black px-4 py-2 rounded hover:bg-yellow-600 font-semibold shadow"
          >
            ⬅ Back to Shop
          </button>
        </div>

        {/* Checkout Box */}
        <div className="bg-gray-800 p-8 rounded-lg shadow-lg text-white">
          <h1 className="text-2xl font-bold mb-6 text-center">Checkout</h1>

          <div className="mb-4">
            <label className="block mb-2 font-semibold">
              Select Payment Method:
            </label>
            <select
              className="w-full p-2 rounded text-black"
              value={paymentMethod}
              onChange={(e) => setPaymentMethod(e.target.value)}
            >
              <option value="card">Card</option>
              <option value="paypal">PayPal</option>
              <option value="cash">Cash on Delivery</option>
            </select>
          </div>

          <div className="mb-4">
            <h2 className="font-semibold mb-2">Your Items:</h2>
            {cart.length === 0 ? (
              <p className="text-red-400">No items in the cart.</p>
            ) : (
              <ul className="space-y-2">
                {cart.map((item, index) => (
                  <li key={index} className="flex justify-between items-center">
                    <span>
                      {item.name} - AED {item.price}
                    </span>
                    <button
                      onClick={() => handleRemoveItem(index)}
                      className="text-red-400 hover:text-red-600 text-sm"
                    >
                      ❌
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </div>

          <p className="text-lg font-semibold mb-4">Total: AED {total}</p>

          <button
            onClick={handlePlaceOrder}
            className="w-full bg-green-500 hover:bg-green-600 py-2 rounded font-semibold"
          >
            Place Order
          </button>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
