// src/pages/Games.js
import React from "react";

const games = [
  {
    title: "Valorant",
    image: "/assets/games/valorant.jpg",
    platform: "PC",
  },
  {
    title: "Call of Duty",
    image: "/assets/games/call-of-duty.jpg",
    platform: "Xbox",
  },
  {
    title: "FIFA 24",
    image: "/assets/games/fifa-24.jpg",
    platform: "PC / Console",
  },
  {
    title: "Elden Ring",
    image: "/assets/games/elden-ring.jpg",
    platform: "PS5 / Xbox",
  },
  // You can add more games here (up to 25+)
];

const Games = () => {
  return (
    <div className="text-white min-h-screen bg-gray-900 py-16 px-8">
      <h2 className="text-3xl font-bold mb-12 text-center">Available Games</h2>

      <div className="grid gap-8 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4">
        {games.map((game, index) => (
          <div
            key={index}
            className="bg-gray-800 border border-gray-700 rounded-lg overflow-hidden shadow-md hover:scale-105 hover:border-emerald-400 hover:shadow-emerald-500/40 transition-all duration-300"
          >
            <img
              src={game.image}
              alt={game.title}
              className="w-full h-48 object-cover"
            />
            <div className="p-4">
              <h3 className="text-xl font-semibold">{game.title}</h3>
              <p className="text-gray-400 text-sm mt-1">{game.platform}</p>
              <button className="mt-4 w-full bg-emerald-400 text-black font-bold py-2 px-4 rounded hover:bg-white transition">
                BOOK
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Games;
