// src/pages/Booking.js
import React, { useState } from "react";
import emailjs from "@emailjs/browser";

const Booking = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    pc: "",
    time: "",
  });

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    emailjs
      .send(
        "service_h5q5lwk",
        "template_b3eg6ds",
        formData,
        "LEjNbva7cItXZoDDR"
      )
      .then(() => {
        alert("✅ Booking confirmed! A confirmation email has been sent.");
        setFormData({ name: "", email: "", pc: "", time: "" });
      })
      .catch((err) => {
        console.error("EmailJS Error:", err);
        alert("❌ Failed to send booking. Try again later.");
      });
  };

  return (
    <div className="text-white min-h-screen bg-gray-900 pt-28 px-8 pb-20">
      <h2 className="text-3xl font-bold mb-12 text-center">
        Book Your Gaming Slot
      </h2>

      <form
        onSubmit={handleSubmit}
        className="max-w-xl mx-auto bg-gray-800 p-8 rounded-lg shadow-md space-y-6"
      >
        <div>
          <label className="block mb-2 text-sm font-semibold">Your Name</label>
          <input
            type="text"
            name="name"
            required
            value={formData.name}
            onChange={handleChange}
            className="w-full p-3 rounded bg-gray-700 border border-gray-600 text-white"
            placeholder="Enter your name"
          />
        </div>

        <div>
          <label className="block mb-2 text-sm font-semibold">Email</label>
          <input
            type="email"
            name="email"
            required
            value={formData.email}
            onChange={handleChange}
            className="w-full p-3 rounded bg-gray-700 border border-gray-600 text-white"
            placeholder="you@example.com"
          />
        </div>

        <div>
          <label className="block mb-2 text-sm font-semibold">Select PC</label>
          <select
            name="pc"
            required
            value={formData.pc}
            onChange={handleChange}
            className="w-full p-3 rounded bg-gray-700 border border-gray-600 text-white"
          >
            <option value="" disabled>
              Select a PC
            </option>
            <option value="PC-1">PC-1</option>
            <option value="PC-2">PC-2</option>
            <option value="PC-3">PC-3</option>
          </select>
        </div>

        <div>
          <label className="block mb-2 text-sm font-semibold">
            Select Time Slot
          </label>
          <select
            name="time"
            required
            value={formData.time}
            onChange={handleChange}
            className="w-full p-3 rounded bg-gray-700 border border-gray-600 text-white"
          >
            <option value="" disabled>
              Select time slot
            </option>
            <option value="2 PM - 3 PM">2 PM - 3 PM</option>
            <option value="3 PM - 4 PM">3 PM - 4 PM</option>
            <option value="4 PM - 5 PM">4 PM - 5 PM</option>
          </select>
        </div>

        <button
          type="submit"
          className="w-full bg-emerald-400 hover:bg-white text-black font-bold py-3 px-6 rounded-full transition"
        >
          Confirm Booking
        </button>
      </form>
    </div>
  );
};

export default Booking;
