import { Link, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import { auth } from "../firebase";
import { onAuthStateChanged } from "firebase/auth";

const Navbar = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });
    return () => unsubscribe();
  }, []);

  const getUserInitial = () => {
    if (!user) return "";
    return user.displayName
      ? user.displayName.charAt(0).toUpperCase()
      : user.email.charAt(0).toUpperCase();
  };

  return (
    <nav className="bg-black text-white px-8 py-4 fixed top-0 left-0 w-full z-50">
      <div className="flex items-center justify-between">
        {/* Left: Logo and Name */}
        <div className="flex items-center">
          <img
            src="/RenderXSvgLogo.svg"
            alt="Render-X Logo"
            className="h-20 w-auto object-contain max-h-12"
          />
          <Link to="/home">
            <span className="text-2xl font-extrabold text-emerald-400 ml-2">
              Render-X
            </span>
          </Link>
        </div>

        {/* Center: Navigation Links */}
        <ul className="flex space-x-8 text-lg items-center">
          {["Home", "Games", "Tournaments", "Booking", "Shop"].map((item) => (
            <li key={item}>
              <Link
                to={`/${item.toLowerCase()}`}
                className="relative group transition"
              >
                {item}
                <span className="absolute left-0 -bottom-1 w-0 h-0.5 bg-blue-500 transition-all duration-300 group-hover:w-full"></span>
              </Link>
            </li>
          ))}
        </ul>

        {/* Right: Auth or Profile Initials */}
        <ul className="flex space-x-6 text-lg items-center">
          {user ? (
            <div
              onClick={() => navigate("/profile")}
              className="h-10 w-10 flex items-center justify-center bg-emerald-500 rounded-full text-white font-bold text-sm cursor-pointer hover:scale-105 transition-transform"
              title="Go to Profile"
            >
              {getUserInitial()}
            </div>
          ) : (
            <li>
              <Link to="/auth" className="relative group transition">
                Register / Login
                <span className="absolute left-0 -bottom-1 w-0 h-0.5 bg-blue-500 transition-all duration-300 group-hover:w-full"></span>
              </Link>
            </li>
          )}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
