import React, { useState } from "react";

const GEMINI_API_KEY = process.env.REACT_APP_GEMINI_API_KEY;

const GameChatBot = () => {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState([
    {
      text: "Hey gamer! Ask me anything about PC builds, games, or accessories!",
      type: "bot",
    },
  ]);
  const [isOpen, setIsOpen] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleSend = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { text: input, type: "user" }];
    setMessages(newMessages);
    setInput("");
    setLoading(true);

    try {
      const response = await fetch(
        `https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=${GEMINI_API_KEY}`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            contents: [
              {
                role: "user",
                parts: [
                  {
                    text: `You're a gaming expert. Only answer gaming-related questions like PC builds, accessories, specs, FPS, monitors, and game optimization. Question: ${input}`,
                  },
                ],
              },
            ],
          }),
        }
      );

      const data = await response.json();
      const botText =
        data?.candidates?.[0]?.content?.parts?.[0]?.text ||
        "Sorry, I didnâ€™t quite get that.";

      setMessages([...newMessages, { text: botText, type: "bot" }]);
    } catch (error) {
      console.error("Gemini API Error:", error);
      setMessages([
        ...newMessages,
        { text: "Something went wrong. Try again later.", type: "bot" },
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") handleSend();
  };

  return (
    <div className="fixed bottom-4 right-4 z-50">
      {isOpen && (
        <div className="w-80 h-[450px] bg-[#0f0f0f] text-white rounded-lg shadow-2xl flex flex-col overflow-hidden">
          <div className="bg-emerald-600 p-3 text-center font-bold">
            ðŸŽ® Gemini Gaming Bot
          </div>
          <div className="flex-1 p-3 overflow-y-auto space-y-2">
            {messages.map((msg, idx) => (
              <div
                key={idx}
                className={`text-sm p-2 rounded-lg ${
                  msg.type === "user"
                    ? "bg-emerald-700 text-right"
                    : "bg-gray-800"
                }`}
              >
                {msg.text}
              </div>
            ))}
            {loading && <div className="text-sm text-gray-400">Typing...</div>}
          </div>
          <div className="p-2 border-t border-gray-700 flex">
            <input
              type="text"
              className="flex-1 p-2 text-black rounded-l text-sm"
              placeholder="Ask about games or PC builds..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={handleKeyPress}
            />
            <button
              onClick={handleSend}
              className="bg-emerald-600 px-4 text-white rounded-r hover:bg-emerald-700"
            >
              Send
            </button>
          </div>
        </div>
      )}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="bg-emerald-600 hover:bg-emerald-700 text-white rounded-full p-4 shadow-lg"
      >
        {isOpen ? "âœ–" : "ðŸ’¬"}
      </button>
    </div>
  );
};

export default GameChatBot;
